import React, { useState, useEffect } from 'react';
import { CountdownCircleTimer } from 'react-countdown-circle-timer'
import '../css/timerbar.css';

const TimerBar = ({ isTestActive, testComplete, resetTest, isTestDone }) => {
  const [seconds, setSeconds] = useState(0);

  useEffect(() => {
    let interval = null;
    if (isTestActive) {
      interval = setInterval(() => {
        setSeconds(seconds => seconds + 1);
      }, 1000);
    } if (seconds === 5) {
      testComplete();
      setSeconds(0);
    }
    return () => clearInterval(interval);
  }, [seconds, isTestActive]);

  return (
    <div className="App">
      {isTestDone ? <button
        onClick={resetTest}> Start Over! </button> :
        <CountdownCircleTimer
          key={isTestActive ? 1 : 0}
          isPlaying={isTestActive}
          duration={5}
          colors={[
            ['#004777', 0.33],
            ['#F7B801', 0.33],
            ['#A30000', 0.33],
          ]}
          onComplete={() => testComplete()}
        >
          {({ remainingTime }) => remainingTime}
        </CountdownCircleTimer>}
    </div>
  );
}

export default TimerBar;
