import React, { useState } from 'react';
import { CountdownCircleTimer } from 'react-countdown-circle-timer'
import '../css/timercircle.css';

const RenderTime = ({ remainingTime, resetTest, resetCircle }) => {
  if (remainingTime === 0) {

    return   <button
      onClick={() => {
        resetTest;
        resetCircle;
      }}> Start Over! </button>
  }
  return (
    <div className="timer">
      <div className="value">{remainingTime}</div>
      <div className="text">seconds</div>
    </div>
  );
};

const TimerCircle = ({ isTestActive, testComplete, resetTest, isTestDone }) => {
  const [key, setKey] = useState(0);

  const resetCircle = () => {
    setKey(key => key + 1);
  }

  return (
    <div>
        <CountdownCircleTimer
          key={key}
          isPlaying={isTestActive}
          duration={2}
          colors={[
            ['#004777', 0.33],
            ['#F7B801', 0.33],
            ['#A30000', 0.33],
          ]}
          onComplete={() => testComplete()}
        >
          {<RenderTime
            resetTest={resetTest}
            resetCircle = { resetCircle }
          />}
        </CountdownCircleTimer>
    </div>
  );
}

export default TimerCircle;
